// import React, { Component } from 'react';
import React, { useContext, useState, useEffect } from 'react';
import styled from "styled-components";
import { Square } from './Square';
import { TictactoeContext } from '../Context';
// import PropTypes from 'prop-types';

//Styled components

const Board = styled.div`
  display: flex;
  justify-content: center;
  text-align: center;
`;

const Content= styled.div`
  display: flex;
  flex-wrap: wrap;
  width: 216px;
`;

const Button = styled.button` 
  padding: 5px 15px;
  margin: 30px auto;
  font-size: 20px;
  background-color: #3c9;
  color: #fff;
  border: none;
  border-radius: 5px;
  :hover {
    background-color: #1a7;
  }
`;

const Players = styled.div``;
const Buttons = styled.div``;

const Body = styled.div`
  ${Players}, ${Buttons} {
    display: flex;
    justify-content: space-around;
  }
`;

const Paragraph = styled.p`
  font-size: 25px;
  border: ${(props) => props.primary} solid 2px;
  border-radius: 5px;
  color: ${(props) => props.primary};
  padding: 2px 15px;
`;

const Banner = styled.div`
  background-color: ${props => props.color};
  color: #fff;
  text-align: center;
  border-radius: 10px;
  width: 100px;
  margin: auto;
  padding: auto 5px;
`;

const Cell = () => {

  // static propTypes = {
  //   player1: PropTypes.string,
  //   player2: PropTypes.string,
  // }

  const [color, setColor ] = useState("#6af");
  const [count, setCount ] = useState(0);
  const [playing, setPlaying ] = useState(true);
  const [board, setBoard ] = useState(["", "", "", "", "", "", "", "", ""]);
  const [winner, setWinner ] = useState("");
  const [player1, player2, actions ] = useContext(TictactoeContext);

  const changeColor = (event) => {
    if (!board[event.target.id] && playing === true) {    
      const newBoard = [...board];
      newBoard[event.target.id] = color;
      // this.setState({ board: newBoard });
      setBoard(newBoard);

      if (color === "#6af") {
        // this.setState({ 
        //   color: "#f6c",
        // })
        setColor("#f6c");  
      } else {
        // this.setState({ 
        //   color: "#6af",
        // })
        setColor("#6af");
      }
      // this.setState( prevState => ({
      //    count: prevState.count + 1 
      // }));
      setCount(count + 1)
    }
  }

  const chooseWinner = () => {
    return color === "#6af" ? player2 : player1;
  }

  // componentDidUpdate () {
  //   const { count, playing } = this.state;
  //   if ((this.conditions() || count === 9) && playing === true) {
  //     this.setState({
  //       playing: false,
  //     })
  //     if (this.conditions()) {
  //       alert(`${ this.chooseWinner() } wins`);
  //       this.setState({ winner: `${ this.chooseWinner() }` })
  //     } else {
  //       alert("Tie");
  //     }
  //   } 
  // }

  useEffect(() => {
    if ((conditions() || count === 9) && playing === true) {
      // this.setState({
      //   playing: false,
      // })
      setPlaying(false);

      if (conditions()) {
        alert(`${ chooseWinner() } wins`);
        // this.setState({ winner: `${ this.chooseWinner() }` })
        setWinner(chooseWinner());
      } else {
        alert("Tie");
      }
    }
  })

  const validation = (num1, num2, num3) => {
    return board[num1] &&
           board[num1] === board[num2] &&
           board[num2] === board[num3]
  }

  const conditions = () => {
    let val = validation;
    return (val(0, 1, 2) || val(3, 4, 5) || val(6, 7, 8) ||
            val(0, 3, 6) || val(1, 4, 7) || val(2, 5, 8) ||
            val(0, 4, 8) || val(2, 4, 6)                   
    );
  }

  const isPlayer1sTurn = () => {
    return color === "#6af"
  }

  const restart = () => {
    // this.setState({ 
    //   playing: true,
    //   board: ["", "", "", "", "", "", "", "", ""],
    //   color: "#6af",
    //   count: 0,
    //   winner: ""
    // })
    setPlaying(true);
    setBoard(["", "", "", "", "", "", "", "", ""]);
    setColor("#6af");
    setCount(0);
    setWinner("");
  }

  return (
    <Body>
      { winner && <Banner color="#1a7">{winner} wins</Banner>}
      { (!winner && count === 9) && <Banner color="#e69500">Tie</Banner>}
      <Players>
        <div>
          <Paragraph primary="#6af">{ player1 }</Paragraph>
          { (isPlayer1sTurn() && playing) && <Banner color="#e69500">Your turn</Banner> }
        </div>
        <div>
          <Paragraph primary="#f6c">{ player2 }</Paragraph>
          { (color !== "#6af" && playing) && <Banner color="#e69500">Your turn</Banner> }
        </div>
      </Players>
      <Board>
        <Content>
          { board.map((num, i) => 
            <Square 
              key={ i } id={ i } color={ num } changeColor={ changeColor }
            />
          ) }
        </Content>  
      </Board>
      <Buttons>
        <Button onClick={ restart }>Restart</Button>
        <Button onClick={ actions.onClickNewGameButton }>New Game</Button>
      </Buttons>
    </Body>
  );
}

export default Cell;
