import React, { Component } from 'react';
import styled from "styled-components";

//Styled components
const Square = styled.div`
  border: solid 1px #000;
  height: 40px;
  width: 40px;
  margin: 1px;
`;

const Row = styled.div`
  display: flex;
`;


class Cell extends Component {

  state = {
    color: "#f00",
    count: 0,
    playing: true,
    board: ["a", "b", "c", "d", "e", "f", "g", "h", "i"],
    player: "Rojo"
  }

  changeColor = (event) => {
    if (!event.target.style.backgroundColor && this.state.true) {
      event.target.style.backgroundColor = this.state.color;
      if (this.state.color === "#f00") {
        this.setState({ color: "#0f0" });
        this.setState({ player: "Rojo" });
      } else {
        this.setState({ color: "#f00" });
        this.setState({ player: "Verde" });
      }
      this.setState({ board: this.state.board.map((sq, id) => (id === Number(event.target.id) ? this.state.color : sq))})
    }
  }

  componentDidUpdate () {
    if (this.conditions()) {
      alert(`${this.state.player} wins`);
      this.setState({
        color: "#f00",
        count: 0,
        playing: false,
        board: ["a", "b", "c", "d", "e", "f", "g", "h", "i"],
        player: "Rojo"
      })
    }
  }

  validation = (num1, num2, num3) => {
    return this.state.board[num1] === this.state.board[num2] && this.state.board[num2] === this.state.board[num3]
  }

  conditions = () => {
    return (this.validation(0, 1, 2) || this.validation(3, 4, 5) || this.validation(6, 7, 8) ||
            this.validation(0, 3, 6) || this.validation(1, 4, 7) || this.validation(2, 5, 8) ||
            this.validation(0, 4, 8) || this.validation(2, 4, 6)                   
    );
  }

  isEven = (number) => {
    return (number % 2 === 0);
  }

  render() {
    return (
      <div>
        <Row>
          <Square id="0" onClick={ this.changeColor }></Square>
          <Square id="1" onClick={ this.changeColor }></Square>
          <Square id="2" onClick={ this.changeColor }></Square>
        </Row>
        <Row>
          <Square id="3" onClick={ this.changeColor }></Square>
          <Square id="4" onClick={ this.changeColor }></Square>
          <Square id="5" onClick={ this.changeColor }></Square>
        </Row>
        <Row>
          <Square id="6" onClick={ this.changeColor }></Square>
          <Square id="7" onClick={ this.changeColor }></Square>
          <Square id="8" onClick={ this.changeColor }></Square>
        </Row>
      </div>  
    )
  }
}

export default Cell;
