import React, { Component } from 'react';
import styled from 'styled-components';

const Button = styled.button`
  display: block;
  border: none;
  background-color: #09f;
  color: #fff;
  font-size: 20px;
  margin: 30px auto;
  padding: 5px 15px;
  border-radius: 3px;
  :hover {
    background-color: #07d;
  }
`;

const Contain = styled.div`
  label, input {
    display: block;
    width: 200px;
    margin: auto;
  }
`;

const Info = (props) => {
  return (
    <Contain>
      <label style={{backgroundColor: "#f00"}} htmlFor="player1">Player 1</label>
      <input onKeyUp={props.onKeyUpInput} type="text" id="player1"/>
      <br/>
      <label style={{backgroundColor: "#0f0"}} htmlFor="player2">Player 2</label>
      <input onKeyUp={props.onKeyUpInput} type="text" id="player2"/>
      <br/>
      <Button onClick={props.onClickStartButton}>Start playing</Button>
    </Contain>
  )
} 

export {
  Info,
  Button
}
