import React, { Component } from 'react';
import styled from "styled-components";
import { Square } from './Square';

//Styled components

const Board = styled.div`
  display: flex;
  justify-content: center;
  text-align: center;
`;

const Content= styled.div`
  display: flex;
  flex-wrap: wrap;
  width: 216px;
`;

const Button = styled.button` 
  padding: 5px 15px;
  margin: 30px auto;
  font-size: 20px;
  background-color: ${(props) => props.primary};
  color: #fff;
  border: none;
  border-radius: 5px;
  :hover {
    background-color: #07d;
  }
`;

const Players = styled.div``;
const Buttons = styled.div``;

const Body = styled.div`
  ${Players}, ${Buttons} {
    display: flex;
    justify-content: space-around;
    margin-top: 30px;
  }
  p {
    font-size: 25px;
  }
`;

class Cell extends Component {
  state = {
    color: "#6af",
    count: 0,
    playing: true,
    board: ["", "", "", "", "", "", "", "", ""],
    player: this.props.player1,
  }

  changeColor = (event) => {

    const { color, playing, board } = this.state;

    if (!board[event.target.id] && playing === true) {    
      const newBoard = [...board];
      newBoard[event.target.id] = color;
      this.setState({ board: newBoard });

      if (color === "#6af") {
        this.setState({ 
          color: "#f6c",
          player: this.props.player1 
        })  
      } else {
        this.setState({ 
          color: "#6af",
          player: this.props.player2
        })
      }
      this.setState({ count: this.state.count + 1 });
    }
  }

  componentDidUpdate () {
    const { count, playing, player} = this.state;
    if ((this.conditions() || count === 9) && playing === true) {
      if (this.conditions()) {
        alert(`${player} wins`);
      } else {
        alert("Tie");
      }
      this.setState({
        color: "#6af",
        count: 0,
        playing: false,
        player: this.state.player1
      })
    } 
  }

  validation = (num1, num2, num3) => {
    let board = this.state.board;
    return board[num1] &&
           board[num1] === board[num2] &&
           board[num2] === board[num3]
  }

  conditions = () => {
    let val = this.validation;
    return (val(0, 1, 2) || val(3, 4, 5) || val(6, 7, 8) ||
            val(0, 3, 6) || val(1, 4, 7) || val(2, 5, 8) ||
            val(0, 4, 8) || val(2, 4, 6)                   
    );
  }

  isEven = (number) => {
    return (number % 2 === 0);
  }

  restart = () => {
    this.setState({ 
      playing: true,
      board: ["", "", "", "", "", "", "", "", ""]
    })
  }

  render() {
    return (
      <Body>
        <Players>
          <p>{ this.props.player1 }</p>
          <p>{ this.props.player2 }</p>
        </Players>
        <Board>
          <Content>
            { this.state.board.map((num, i) => <Square key={i} id={i} color={num} changeColor={ this.changeColor }/>) }
          </Content>  
        </Board>
        <Buttons>
          <Button primary={this.state.color} onClick={this.restart}>Restart</Button>
          <Button primary={this.state.color} onClick={this.props.onClickNewGameButton}>New Game</Button>
        </Buttons>
      </Body>
    )
  }
}

export default Cell;
