import React, { Component } from 'react';
import { Jumbotron } from 'react-bootstrap';
import Cell from './components/Cell';
import Info from './components/Info';

class App extends Component {
  state = {
    start: false,
    readyToPlay: false,
    player1: "",
    player2: ""
  }

  onKeyUpInput = (event) => {
    if (event.target.id === "player1") {
      this.setState({ player1: event.target.value });
    } else {
      this.setState({ player2: event.target.value });
    }
  }

  onClickStartButton = () => {
    if (this.state.player1 && this.state.player2) {
      this.setState({ readyToPlay: true });
    } else {
      alert("You must have 2 players to start the game!");
    }
  }

  onClickNewGameButton = () => {
    this.setState({ readyToPlay: false, player1: "", player2: "" })
  }

  render() {
    return (
      <div>
        <Jumbotron>
          <h1>Tic Tac Toe!</h1>
          <p>This is a simple tic tac toe game.</p>
        </Jumbotron>
        <button>Start</button>
        { 
          this.state.readyToPlay === false ?
          <Info onKeyUpInput={this.onKeyUpInput}
                onClickStartButton={this.onClickStartButton} 
          /> :
          <Cell onClickNewGameButton={this.onClickNewGameButton}/>
        }
      </div>
    );
  }
}

export default App;
