import React from 'react';
import styled from 'styled-components';

const Col = styled.div`
  border: solid 1px #000;
  border-radius: 4px;
  height: 70px;
  width: 70px;
  margin: 1px;
  cursor: pointer;
  :hover {
    border: #000 3px solid;
  }
`;

const Div = styled.div`
  background: url(${props => props.img}) center center/cover;
`;

const Square = ({ id, color, changeColor }) => {  
  return (
    <Div img={ color === "#6af" ? 'https://www.onlygfx.com/wp-content/uploads/2018/10/6-watercolor-x-brush-stroke-4.png' : 
               color === "#f6c" ? 'https://www.pinclipart.com/picdir/big/38-382953_pink-circle-by-pink-circle-wellness-holistic-wellness.png' :
               color === ""
   }>
      <Col id={ id }
           onClick={ changeColor }
      >
      </Col>          
    </Div>
  );
}

export { Col, Square }
