import React, { Component } from 'react';
import styled from './styled-components';

const Col = styled.div`
  border: solid 1px #000;
  height: 40px;
  width: 40px;
  margin: 1px;
  cursor: pointer;
`;

class Square extends Component {
  state = {
    color: ""
  }

  render() {
    return (
      <Col id="0" onClick={ this.changeColor }></Col>
    );
  }
}

export default Square;
