import React, { Component } from 'react';
import styled from "styled-components";


const Square = styled.div`
  border: solid 1px #000;
  height: 40px;
  width: 40px;
  margin: 1px;
`;

const Row = styled.div`
  display: flex;
`;


class Cell extends Component {

  state = {

  }

  changeColor = (event) => {
    event.target.style.backgroundColor = "#f00";
  }

  render() {
    return (
      <div>
        <Row>
          <Square id="0" onClick={ this.changeColor }></Square>
          <Square id="1" onclick="changeColor(this)"></Square>
          <Square id="2" onclick="changeColor(this)"></Square>
        </Row>
        <Row>
          <Square id="3" onclick="changeColor(this)"></Square>
          <Square id="4" onclick="changeColor(this)"></Square>
          <Square id="5" onclick="changeColor(this)"></Square>
        </Row>
        <Row>
          <Square id="6" onclick="changeColor(this)"></Square>
          <Square id="7" onclick="changeColor(this)"></Square>
          <Square id="8" onclick="changeColor(this)"></Square>
        </Row>
      </div>  
    )
  }
}

export default Cell;
